INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description`, `click`, `order_id` ) VALUES  ('7', '4', '公司简介', '<h1 style=\"text-align:center;text-indent:2em;\">\r\n	关于我们\r\n</h1>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-size:14px;\">黎华家具装饰材料城成立于</span><span style=\"font-size:14px;\">2001</span><span style=\"font-size:14px;\">年，位于哈尔滨市道外区东直路</span><span style=\"font-size:14px;\">1</span><span style=\"font-size:14px;\">号，地理位置优越，是道外区黄金商圈的中心，是黑龙江省规模较大的一家集家具、装饰材料为一体的大兴专业商城之一。商城秉持“诚信为本，先做人，后经商”的经营理念，致力于为消费者奉献一个诚信、专业的品牌家具装饰材料商城。商城主要经营精品家具、办公家具、地板、涂料壁纸、橱柜一条街、板材基地、五金、精品卫浴陶瓷广场等近十多项特色区域。多年来黎华家具装饰材料城始终以良好的经营理念，打造百姓首选的家具建材商场，贴近民心，以优质的品质低价格的销售和永不间断的售后服务使消费者在黎华购物享受到真正的实惠。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-size:14px;\">商城服务热线：</span><span style=\"font-size:14px;\">400-6455-999</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-size:14px;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-size:14px;\"><img src=\"/images/upload/image/20150726/20150726070622_13010.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-size:14px;\"><br />\r\n</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-size:14px;\"><img src=\"/images/upload/image/20150813/20150813064905_27053.png\" alt=\"\" /><br />\r\n</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-size:14px;\"><br />\r\n</span> \r\n</p>', '', '', '', '0', '1', '1385536786', '', '0', 'http://', '', '0', '0');
