INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description`, `click`, `order_id` ) VALUES  ('103', '9', '黎华家具装饰材料城“乐享五一，低价出击”大型促销活动完美收官', '<p style=\"text-indent:2em;\">\r\n	<span style=\"color:#333333;font-family:Arial;font-size:14px;line-height:24px;background-color:#FFFFFF;\">2016年4月30日—5月2日，黎华家具装饰材料城“乐享五一，低价出击”大型促销活动完美收官。本次活动吸引了上万人次的消费者前来选购家具，成交金额也创造了历届活动新高，本次活动不仅力度大，且形式新颖，以“刮刮乐”和“砸金蛋”作为双核心，一方面顾客享受刮大奖的紧张刺激，另一方面还有砸金蛋、低价换购等一系列的促销活动，使消费者真正体会到了高兴而来、满意而归的感受。</span>\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<span style=\"color:#333333;font-family:Arial;font-size:14px;line-height:24px;background-color:#FFFFFF;\"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:Arial;font-size:14px;line-height:24px;background-color:#FFFFFF;\"><img src=\"http://lihuajiajucheng.com/images/upload/image/20160504/20160504074739_49992.jpg\" width=\"650\" height=\"473\" alt=\"\" /><img src=\"http://lihuajiajucheng.com/images/upload/image/20160504/20160504074846_92985.jpg\" width=\"650\" height=\"488\" alt=\"\" /><img src=\"http://lihuajiajucheng.com/images/upload/image/20160504/20160504075000_58359.jpg\" width=\"650\" height=\"488\" alt=\"\" /><img src=\"http://lihuajiajucheng.com/images/upload/image/20160504/20160504075039_78271.jpg\" width=\"650\" height=\"488\" alt=\"\" /><img src=\"http://lihuajiajucheng.com/images/upload/image/20160504/20160504075105_36625.jpg\" width=\"650\" height=\"482\" alt=\"\" /><br />\r\n</span>\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<span style=\"color:#333333;font-family:Arial;font-size:14px;line-height:24px;background-color:#FFFFFF;\">活动能够取得圆满成功，黎华家具装饰材料城衷心感谢每一位顾客对商城的信任与支持，我们会在今后的活动中，一如既往为顾客朋友们提供优质的商品、热诚的服务以及完善的售后保障。</span>\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>', '黎华家具城', '', '', '0', '1', '1465084484', '', '0', 'http://', '', '0', '0');
