INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description`, `click`, `order_id` ) VALUES  ('95', '9', '黎华家具城十一大型促销活动圆满结束！', '<p style=\"font-size:14px;vertical-align:baseline;text-indent:2em;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	<span style=\"vertical-align:baseline;\">&nbsp;2015年10月1日至10月7日国庆节期间，黎华家具装饰材料城以“十一约惠黎华，家具、建材抄底价”为主题的大型促销活动圆满落幕，并获得了巨大的成功。此次活动，满足了所有购买家具需求的百姓，使得众多消费者纷纷聚集于此，床垫、沙发、实木床、家具、装饰材料等商品均以抄底价进行售卖，简约现代风格、欧式风格、古典风、田园风格应有尽有，满足不同家庭装修风格的需求。</span><br />\r\n<img src=\"http://www.lihuajiajucheng.com/images/upload/image/20151015/20151015154648_97294.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;vertical-align:baseline;text-indent:2em;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	<span style=\"vertical-align:baseline;\">10月1日—10月7日活动期间，商城还准备了丰厚的奖品，只要在黎华家具装饰材料城消费的顾客朋友均可在服务中心进行抽奖活动，冰箱、洗衣机、电磁炉、蚕丝被等众多大奖豪爽派送，地垫、松木框、购物车等商品一元出售，那力度真是杠杠的！这么大的优惠活动，瞬间点燃全场，成交额远远超出预期！</span>\r\n</p>\r\n<p style=\"font-size:14px;vertical-align:baseline;text-indent:2em;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	<span style=\"vertical-align:baseline;\"><img src=\"http://www.lihuajiajucheng.com/images/upload/image/20151015/20151015154713_55747.jpg\" alt=\"\" /><br />\r\n</span>\r\n</p>\r\n<br />\r\n<span style=\"font-size:14px;vertical-align:baseline;color:#333333;font-family:Arial;line-height:24px;background-color:#FFFFFF;\">感谢所有光临黎华家具装饰材料城的新老顾客朋友们，感谢你们多年来一如既往的支持黎华家具装饰材料城，我们在以后的日子里，将会奉献更多、更好的优惠促销活动，想知道更多的详细活动信息，请持续关注“黎华家具装饰材料城”微信公众平台。</span><span style=\"color:#333333;font-family:Arial;font-size:14px;line-height:24px;background-color:#FFFFFF;\"></span>\r\n<p style=\"font-size:14px;vertical-align:baseline;text-indent:2em;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;vertical-align:baseline;text-indent:2em;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	<span style=\"vertical-align:baseline;line-height:21px;\"><img src=\"http://www.lihuajiajucheng.com/images/upload/image/20151015/20151015154750_57837.jpg\" alt=\"\" /><img src=\"http://www.lihuajiajucheng.com/images/upload/image/20151015/20151015154812_87926.jpg\" width=\"447\" height=\"596\" alt=\"\" /><br />\r\n</span><br />\r\n<span style=\"vertical-align:baseline;\">黎华家具装饰材料城主要经营项目:家具区、地板区、门区、精品卫浴广场、精品家具区、办公家具区、涂料壁纸区、橱柜一条街、五金区、板材基地等家具建材商品，是黑龙江省最大的一家集家具、装饰建材为一体的大型专业商城之一，多年来一直秉承良好的经营理念，打造百姓首选的家具建材商场。黎华家具装饰材料城以价格低、品种全、质量优、服务好的特点，成为百姓首选的家具建材商城，多年来得到了广大消费者认可。</span><br />\r\n<span style=\"vertical-align:baseline;\">公交线路：115路;&nbsp;116路;&nbsp;121路兴隆八道街站下车</span><br />\r\n<span style=\"vertical-align:baseline;\">4路;&nbsp;35路;&nbsp;61路;&nbsp;70路;&nbsp;92路;&nbsp;115路;&nbsp;116路;&nbsp;121路;&nbsp;205路;&nbsp;357路;&nbsp;359路;&nbsp;郊9支4市老年医院站下车商城服务热线：400-6455-999客服中心热线：86102266&nbsp;&nbsp;&nbsp;86102277&nbsp;&nbsp;松北分店地址：松北大道211号（省农机对面）&nbsp;&nbsp;&nbsp;&nbsp;松北热线：87165611</span>\r\n</p>', '黎华家具城', '', '活动', '0', '1', '1445061478', '', '0', 'http://', '', '0', '0');
