INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description`, `click`, `order_id` ) VALUES  ('101', '9', '黎华家具城 巅峰夜总惠（第二季）', '<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;text-indent:2em;\">\r\n	<b><span style=\"color:#E53333;font-size:16px;\">黎华家具城</span></b>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;text-indent:2em;\">\r\n	<b><span style=\"color:#E53333;font-size:16px;\">巅峰夜总惠（第二季）</span></b>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;text-indent:2em;\">\r\n	<b><span style=\"font-size:14px;\">精品家具抢单大惠</span></b>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	活动主题：<b>巅峰夜总惠——精品家具抢单大惠</b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	活动落地时间：4月23日晚17:30-21:00\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	店面集客时间：4月15日-4月23日\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	活动推广时间：4月15日-4月23日\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	活动政策：\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<b>巅峰刮刮乐：<span style=\"color:#E53333;\">巅峰头彩刮刮乐特供好礼</span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	活动当晚凭巅峰夜总惠VIP团购卡到场顾客每人可获赠3张“大牌刮刮乐”抽奖机会，在20家品牌中任意刮开1家，均有精美家居礼品赠送（每张刮刮卡仅限刮开一次，到对应品牌店面领取）。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<b>增值返现：<span style=\"color:#E53333;\">预存</span><span style=\"color:#E53333;\">10</span><span style=\"color:#E53333;\">元立返</span><span style=\"color:#E53333;\">100</span><span style=\"color:#E53333;\">元再送订单礼</span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	活动期到各品牌店面预存10元即可获得100元巅峰夜总惠“巅峰提货卡”，持卡当晚在参与品牌购物即可在最低成交价基础上再省100元。凭“巅峰提货卡”及订单可领取价值180元景德镇陶瓷厨具一套。<b></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<b>零利特供：<span style=\"color:#E53333;\">厂价零利风暴，限时神秘惊爆夜卖</span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	活动当晚各品牌特别推出“厂价零利”神秘商品，超低惊爆，限时1小时夜卖，仅限活动当晚特供，业主凭VIP团购卡零利认购栏投入零利认购箱，当晚19:00开始现场抢购。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<b>联购赠礼：<span style=\"color:#E53333;\">联购满额再赠好礼</span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	活动当晚购物交款满额3000元即可参与联购赠礼：\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	联购2单满额3000元即赠精品陶瓷茶具\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	联购2单满额5000元即赠精品记忆枕\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	联购2单满额10000元即赠品牌蚕丝被\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	联购2单满额15000元即赠精品电烤锅\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	联购2单满额20000元即赠西门子咖啡机\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<b>满额免单：<span style=\"color:#E53333;\">购物抽免单</span><span style=\"color:#E53333;\">+</span><span style=\"color:#E53333;\">家电大奖</span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	购物满额3000元即可参与免单幸运红包，抽取免单+家电大奖。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	一等奖：4999元现金免单\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	二等奖：乐视第3代超级电视\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	三等奖：美的立式消毒碗柜\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	四等奖：家用小冰箱\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<img src=\"/images/upload/image/20160420/20160420130137_90021.png\" alt=\"\" /><img src=\"/images/upload/image/20160420/20160420130155_54772.png\" alt=\"\" />\r\n</p>', '黎华家具城', '', '活动', '0', '1', '1461128522', '', '0', 'http://', '', '0', '0');
