INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description`, `click`, `order_id` ) VALUES  ('104', '9', '黎华家具城 巅峰夜总惠（520 约惠之夜）', '<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;text-indent:2em;\">\r\n	<b><span style=\"font-size:16px;\">精品家具内购夜宴</span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	活动主题：<b>巅峰夜总惠——520约惠之夜——精品家具内购夜宴</b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	活动落地时间：5月20日晚18:00-21:00\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	活动政策：\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<b>到场送礼：<span style=\"color:#E53333;font-size:14px;\">巅峰夜宴到场特供伴手礼：</span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	即日起至5月20日到黎华家具城一层服务中心免费领取邀请函，5月20日晚18:00到场即有好礼赠送，凭邀请函还可免费获得一张抽奖券，获得一次免费抽奖机会。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<b>到场返现：<span style=\"font-size:14px;color:#E53333;\">持邀请函到场即赠返现卡：</span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	活动期持邀请函到各品牌店面可免费领取巅峰夜总惠“巅峰返现卡”，持卡当晚在参与品牌购物即可在最低成交价基础上再省100元。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<b>零利特供：<span style=\"color:#E53333;font-size:14px;\">厂价零利</span><span style=\"color:#E53333;font-size:14px;\">3</span><span style=\"color:#E53333;font-size:14px;\">小时私密放价：</span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	活动当晚各品牌特别推出“厂价零利”神秘商品，私密价超低惊爆，限时夜卖，仅限活动当晚特供。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<b>购物赠礼：<span style=\"font-size:14px;color:#E53333;\">当晚购物满额再赠好礼：</span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	活动当晚购物交款满额2000元即可参与满额赠礼：\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	累计消费满额2000元即赠精品陶瓷茶具\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	累计消费满额3000元即赠精品记忆枕\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	累计消费满额5000元即赠品牌蚕丝被\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	累计消费满额10000元即赠精品电烤锅\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	累计消费满额15000元即赠西门子咖啡机\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<b>满额免单：<span style=\"font-size:14px;color:#E53333;\">购物抽免单</span><span style=\"font-size:14px;color:#E53333;\">+</span><span style=\"font-size:14px;color:#E53333;\">家电大奖：</span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	购物缴纳全款满1000元即可获得一张抽奖券，满2000元可获得两张，依次类推，8张封顶，参与免单幸运红包，抽取免单+家电大奖。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	一等奖：4999元现金免单\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	二等奖：乐视第3代超级电视\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	三等奖：美的立式消毒碗柜\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	四等奖：家用小冰箱\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	幸运奖：幸运家居礼包\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<img src=\"http://lihuajiajucheng.com/images/upload/image/20160510/20160510075950_59509.jpg\" width=\"650\" height=\"872\" alt=\"\" />\r\n</p>', '黎华家具城', '', '', '0', '1', '1465084539', '', '0', 'http://', '', '0', '0');
