INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('1', '凯撒豪庭', '1385821836877586354.jpg', '迷醉在高贵的魅惑中，恢弘的气势包围着身心的愉悦。流年锦心，在似水的年华中感悟到一种浩大的艺术境界！\r\n生产基地：中国现代家具之父—鼎盛家具集团', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('2', '韩菲尔', '1385821846656400819.gif', '田园生活的意境、现代生活的情调 在一杯咖啡、一杯香茗的时间里一 一展现 在韩菲尔的世界，每一个转身都是自然主义的本色演绎！\r\n生产基地：金华庭家具集团', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('3', '蒂美悦', '1385821871691649220.gif', '关注时尚、风格独立，在注重家居现实感受的同时更善于在平凡事物上创造精致的品位。\r\n生产基地：香港圣维纳斯家具集团', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('4', '卡富亚', '1385821882418420829.jpg', '乐观、开朗，在现代生活中寻找到最自我的一面，在随性的风格中坚守不一样的品质体验，让现代家具拥有鲜明的个性特点。\r\n生产基地：“SOHO时尚派”—耀邦家具集团', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('5', '安派尔地板', '1437529184669996325.png', '在追求时尚的过程中，致力于打造经典，营造出具有浓郁的东方情调和气韵的安派尔地板，让每一位选择安派尔的用户可以营造舒适的空间，并赋予房间时尚经典的现代精神，让流行与时尚完美的结合在一起。4/精品追求雅致的旋律，内敛而高贵的文化精髓渗进每一寸木纹、纤维，写满你居室的每一个空间。当你踏进它的上面，操劳的紧张与疲惫稀释于无形，得新唤起了你的从容，婉约和温情。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('6', '傲美地板', '1437529173630857409.png', '傲美地板以“专业、诚信、创新、服务”为宗旨，以人为本、客户至上、锐志进取、团结拼搏、争创一流的企业精神，坚持“科技为第一生产力”的企业发展理念，为您展示理想的空间，实现您梦寐以求的境界。傲美地板不断革新营销模式，走品牌化发展道路，始终保持品牌活力，立足国内，发展国外，为打造中国木业航母而奋斗。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('7', '巴厘岛印象', '1437529152888050670.png', '以市场为导向，以创新为动力，以品质为生命”是公司一贯坚持的方针，“以人为本，积极进取”是公司的核心文化。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('8', '宝利沙发', '1437529275230037223.png', '宝利家具有限公司，创建于1989年，“香港宝利”品牌创立于2003年，公司现已发展为集研发、设计，生产、销售为一体的专业化软体家具制造企业。公司所有产品均采用现代高质量生产材料，运用全新科技生产机械和生产技术颉取各种思维自由设计配套产品统一、整体的风格，通过工艺和材料的灵活运用，融入现代时尚的流行元素及配色理论。并形成配套产品', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('9', '帝龙地板', '1437529314902264346.png', '帝龍地板在基材的选择上精益求精，采用高密度、抗冲击性强，良好的内结合度决定了地板的超长寿命，优异表面性决定了表面的平整性和光洁度，高密度也决定了地板的强度和永不变形的稳定性。\r\n时尚与潮流同步一直是帝龍地板对产品花色的至上追求，精选国际顶级花纹装饰纸，独特的木纹处理技术，呈现出风格不同的面层效果。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('10', '多乐士', '1437529330787356953.png', '阿克苏诺贝尔是全球领先的油漆和涂料企业，也是专业化学品的主要生产商。我们为全球工业与广大消费者供应创新产品，全力投入为客户打造各种可持续发展的解决方案。我们旗下品牌阵容鼎盛，拥有多乐士(Dulux)、新劲(Sikkens)、国际(International) 和依卡(Eka) 等着名品牌。阿克苏诺贝尔总部设在荷兰阿姆斯特丹，我们一贯在可持续发展领域保持领先。我们广布全球80多个国家的50,000名员工力争提供领先的产品和技术，满足快速变化的市场中不断增长的需求。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('11', '哆唻咪', '1437529347905698021.png', '哆唻咪， 简单的开始，朴素的体验，如一支歌陪伴童年岁月。“哆来咪”蕴涵双层意义，一方面从最简要的设计起手，为孩子设计出风格简约，轻快活泼的家具；另一方面 从孩子最基本的需要出发，设计出变化多样，适宜不同年龄段的系列家具。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('12', '福顺凯旋', '1437529362440155815.png', '福顺凯旋木业具有8年以上生产经验的厂家，主要生产实木复合喷漆门及所有木制作等等，产品生产严格执行国家同行业标准。企业宗旨：以时间为生命、以质量求生存、以优价争市场、以信誉扩业务。全力打造木门业高端品牌！ ', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('13', '光明家具', '1437529385658770857.png', '光明家具坚持“绿色、环保、优质、实用”的设计理念，与国内多家设计机构合作，设计具有中国文化特色的新东方新中式实木原创家具。\r\n\r\n光明家具始终坚持“星级服务，始于主动，寓于真诚，终于满意”的服务理念', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('14', '宏益床垫', '1437653829177396755.png', '宏益实业集团恪守“大音希声，大象无形”的企业形象，“将诚实、勤劳、责任心、敬天爱人”的思想贯彻在集团的实际运营中，潜心专注产品研发和客户服务，将精致与厚重、体验与品质融入到企业发展的每一个细节之中。现已通过“质量管理体系认证”、“环境管理体系认证”、“职业健康安全管理体系认证”、“中国环保产品认证”、“中国绿色材料认证”等众多品质认证，并荣获“中国著名品牌”、“中国名牌”、“中国诚信企业500家”、“中国床垫行业十大品牌”、“绿色环保首选品牌”等多项殊荣。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('15', '华润漆', '1437529446694162346.png', '华润漆品牌始创于1991年，产品系由专业生产建筑装饰装修涂料、高档木器涂料、水性涂料及高科技工业涂料的高新技术企业所生产。华润漆采用欧洲最先进的生产设备和自动化配色系统制造,产品产销量长期位居国内前列, 木器涂料更是稳居国内行业龙头地位。\r\n“华润漆”是国内最大的涂料品牌之一，20多年来，华润漆凭借一流的品质和服务能力受到广大专业人士和消费者推崇。华润漆倡导“漆业真专家”的品牌理念，致力于提供全系列真正环保的产品和全程顺心涂装服务。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('16', '吉祥百得', '1437529481026858510.png', '公司将以中国家具协会授予南康“中国中部家具产业基地”和国家质检总局批准南康为“全国实木家具品牌创建示范区”为契机，秉持“品德至上、品质至精、品牌至美、品味至高”的企业宗旨，按照“诚信、创新、高效、共赢”的经营理念，奋力将公司打造成中国家具行业的领军品牌和龙头企业。\r\n“吉祥百得”正是立足这种境界，一路浩歌，征战南北，攻城略地，抢占了一个个市场的制高点，品牌认知率和市场占有率正向一线品牌迈进。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('17', '金凯莱', '1437529610667816146.png', '金凯莱床垫制造有限公司是一家集产品研发、生产、销售和完善的售后服务为一体的综合性企业。\r\n        公司拥有先进的生产设备和一批高素质的管理和生产技术人才，多年以来，公司以追求卓越，与时俱进、开创未来为企业理念，诚信经营、贴心服务、客户获利、员工满意、为服务社会、服务员工为企业宗旨。\r\n        金凯莱系列床垫、设计新颖、时尚，结构合理，符合人体工学原理，全部选用的上等原辅材料、严格执行ISO9001-2000国际质量管理标准，生产出的各种规格款式的高品质床垫、深受广大消费者的喜爱，完善的售后服务体系保证了消费者无后顾之忧。 \r\n        金凯莱系列床垫产品多次在哈尔滨家具博览会上获得奖杯，先后被评为“黑龙江名牌产品”、中国名优家居企业协会“推荐产品”；中国名优家居产业协会会员单位，哈尔滨家具行业协会常务理事单位。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('18', '凯达美家', '1437529624638699570.png', '“凯达美家”品牌个性  \r\n    休闲时尚、品味生活 —— 时尚表明一个人对当代生活的投入与把握能力，一个与时代格格不入的人是谈\r\n    不上魅力的，而懂得品味生活的人必定热爱生活，同时更懂得如何的享受生活。  \r\n    成熟、稳重求实 —— 成熟在某种程度上意味着接近完美，稳重求实带给人一种厚重的、完全可以信赖的\r\n    力量感。  \r\n    优雅、迷人风度 —— 优雅显示了“凯达美家”个性中具有魅力一面，显示出雍容、自信与从容不迫。而\r\n    风度则展现出“凯达美家”使用者的个人素养和气质。 ', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('19', '欧迪兰', '1437529651503192805.png', '欧迪兰沙发创建于2002年，集高端的设计，精致的品质和热情的服务于一体，主要以生产真皮沙发、欧式沙发、皮布、布艺休闲沙发为主，为广大消费者精诚服务，买的实惠用的舒服，欧迪兰沙发您一生的选择。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('20', '上品印象', '1437529685818881490.png', '上品印象设计新颖、典雅、符合人体力学，全部选用上等原材料，全部原材料、辅料都经过有关部门的严格检测。上品印象旗下有七大系列数十种产品。这些系列产品都深受广大消费者们的喜爱，上品印象完善的售后服务体系免去了消费们的后顾之忧。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('21', '松霖家具', '1437529706806942955.png', '\r\n徐州松霖家私有限公司采用100%质地细，生长周期长，北纬60度以上的寒带俄罗斯樟子松为原材料。经选材.烘干，脱脂到成品均经过公司严重质量控制，从而保障了产品质量和消费者利益。徐州松霖家私有限公司家具设计简洁明快，线条圆滑流畅，做工精致细腻，注重环保健康，倡导绿色家居生活，让您时时能感受到松木家具特有的魅力与风情。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('22', '泰兴明邦', '1437529728745003182.png', '成都泰兴明邦家私有限公司 经销批发的电视柜、茶几畅销消费者市场，在消费者当中享有较高的地位，公司与多家零售商和代理商建立了长期稳定的合作关系。成都泰兴明邦家私有限公司经销的电视柜、茶几品种齐全、价格合理。成都泰兴明邦家私有限公司实力雄厚，重信用、守合同、保证产品质量，以多品种经营特色和薄利多销的原则，赢得了广大客户的信任。\r\n', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('23', '顽皮狗', '1437529751907597052.png', '公司先后获得中国家具行业环保品牌、中国家具行业十佳客厅品牌、家具畅销品牌、等省、市、地方多项荣誉。公司以质量求生存，信誉求发展，投放市场之产品因其质量上乘、价格适中而深受消费者青睐，名冠依托精湛的技术、先进的设备、专业的团队、完善的服务，努力打造家具行业“诚信品牌”的典范。力求为您构建一个时尚、舒适、温馨的家园氛围。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('24', '夏凡尼', '1437529773732519477.png', '夏凡尼品牌起源于20世纪的巴黎，是以法国著名家具设计师夏凡尼的名字命名的，他是一位对家居艺术充满狂热且极富创造力的艺术家，他设计的家具风格高贵典雅，柔和浪漫，营造一种精致的、时尚的、主流的生活体验，传递出色的生活品味。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('25', '小憨豆', '1437529789051973182.png', '小憨豆倡导的是快乐的居家生活，小憨豆给顾客带来的是健康，带来的是喜悦，带来的是财气，带来的是方便。因为家具买回去是和家人生活在一起。如果产品质量有问题，使用起来就会给您带来不方便。因此小憨豆快乐文化就是为了顾客带来方便的快乐家居生活。其功能人性化的设计，使用方便，质量有保证，国家质量环保认证。高品质的服务给您和您的家人带来的是健康和快乐。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('26', '鑫宝家具', '1437529808234003849.png', '鑫宝整体家具，以中西融合的原创设计，缔造着简约、舒适的现代生活。   \r\n鑫宝整体家具，以不断创新的企业精神，引领中国家具行业的发展潮流。   \r\n鑫宝整体家具，以绿色环保的家居理念，在家居舞台演绎五彩缤纷之美。　', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('27', '星星木门', '1437529828927878570.png', '重庆星星集团公司是中国木门行业产销规模最大的生产企业。拥有41项专利技术，资产总额7亿多元，员工5000多名，年产套装门200万套。先后评为国家级农业产业化重点龙头企业，重庆市首批知识产权试点企业、首批循环经济试点企业、首批创新型试点企业。 　　公司主导和引领中国木门行业的发展。最先开发室内装饰套装门，为重庆市赢得了“中国套装门之都”殊荣;连续多年推选为中国木门协会副会长、重庆市木门协会执行会长、中国建材协会常务理事;参与制定了中国木门窗4个行业技术标准和1个国家技术标准;先后荣获国际国内20多项大奖，星星套装门畅销全国31个省市，出口10多个国家及地区。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('28', '阳光林森', '1437529844962921955.png', '阳光林森家具是成都市林森木业有限责任公司打造的中高端民用家具知名品牌，公司于1992年成立，已发展成集研发，生产，营销为一体，具有核心竞争力和可持续发展的中国家具行业大型集团化公司。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('29', '忠国三新', '1437529871120712693.png', '企业核心理念是--致力于开发人类的健康睡眠。作为一家专业生产床垫的大型企业，“三新”的责任和使命，概括来讲，主要有产品使命、经济使命和社会使命。将三新企业的产品使命、经济使命和社会使命有效的融合和升华，从而形成“三新”独特的企业核心理念，即致力于人类的健康睡眠，意味着三新的每一件产品，每一次创新，每一项服务，都必须紧密围绕改善消费者健康而展开，不仅要能够满足消费者使用“三新”产品的功能需求，同时还能提升消费者的生活质量，带来更健康更有品质的生活方式。 ', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('30', '伊春实木', '1438657035467397678.jpg', '伊春实木，主要经营各种实木家具，工厂坐落在中国著名的“天然氧吧”伊春市。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('31', '阳光信基木业', '1438655766190148728.jpg', '阳光信基木业目前是一家致力于现代话桌椅设计开发、生产、销售为一体的专业企业。自新兴木业产品进入各大中城市以来，深受各企业和群众的好评。 \r\n　　本公司拥有国内先进生产设备和专业技术人员，生产格式高中档餐桌椅、大理石桌、纯实木桌椅。可根据客户的不同需求，加工各种型号及规格产品，以质优价廉的产品满足客户需求。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('32', '质诚办公', '1438655803469348393.png', ' 环保节能：质诚身于环保事业，产品均能做到通过第三方权威认证的室内空气质量认证。我们在产品设计、工艺选材与制造销售的每一个环节上都积极履行绿色低碳，确保可回收度、低挥发量与经久耐用，为绿色建筑加分。同时，我们以五年免费保用为基准，切实承担起可持续发展的环保责任。\r\n    产品系列：20年专注于办公台系列、办公椅系列、文件柜系列、货架系列、屏风系列、隔断系列，接待台等中高档办公家具，并定做异形柜上百种规格。出自“质诚”的产品都能达到美观与坚固耐用并存，保证了产品的高品高质', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('33', '欧帝厨卫', '1438655865545822706.jpg', '欧帝电器坚持对厨房电器产品的人性化研发，为厨房向科技化、人性化的方向发展而努力。以技术上的不断创新和突破为用户带来真正的价值，更为中国家庭带来了与世界同步的厨房文化和高品质的生活体验。欧帝，打造人性化的完美厨房空间，创造价值，实现梦想！', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('34', '欧普照明', '1438655885548453559.jpg', '欧普照明始于1996年，是一家集研发、生产和销售于一体的综合型照明企业。历经18年的发展，公司现有员工6000多人，拥有上海总部及中山工业园、吴江工业园等多个生产基地；公司产品涵盖LED及传统光源、灯具、电工电器、吊顶产品等领域。作为中国照明行业领先的整体照明解决方案提供者，欧普照明不仅致力于研究光的合理运用，提供贴心产品，还为消费者提供差异化整体照明解决方案等专业的配套服务，全面提升用户体验。针对不同场合，欧普提供的照明方案能满足人在不同时间、不同空间生理需求和心理需求的灯光。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('35', '海尔', '1438655944662575206.jpg', '海尔秉承锐意进取的海尔文化，不拘泥于现有的家电行业的产品与服务形式，在工作中不断求新求变，积极拓展业务新领域，开辟现代生活解决方案的新思路、新技术、新产品、新服务，引领现代生活方式的新潮流，以创新独到的方式全面优化生活和环境质量。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('36', '美洛芙', '1439691225697332158.jpg', '公司拥有先进的生产设备，现代化的标准厂房和一支经验丰富的工程设计与生产队伍。\r\n　　设计上亨通致力于寻求艺术和实用的大原则，凭借产品新颖的款式，严谨的工艺，过硬的质量，不仅国内市场销路良好，更是深得国外客户的赞誉。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('37', '双利陶瓷', '1440557080753627746.jpg', '双利陶瓷有限公司尊崇“踏实、拼搏、责任”的企业精神，并以诚信、共赢、开创经营理念，创造良好的企业环境，以全新的管理模式，完善的技术，周到的服务，卓越的品质为生存根本，我们始终坚持用户至上 用心服务于客户，坚持用自己的服务去打动客户。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('38', '立名办公', '1439691313141813399.jpg', '立名办公家具有限公司座落在中国家具名镇佛山市，成立于1999年，是一家以生产办公家具为主的专业家具生产配套公司，采用先进的机器设备和管理技术，生产环保的办公屏风、办公椅、文件柜、会议台、办公台和接待台，公司经过十多年的发展，已经汇集大批专业设计制造、市场营销和企业管理专业人才，目标是将时尚的设计和高效的办公环境融为一体，使每件产品尽显舒适、新颖、实用。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('39', '适维浴屏', '1439691336379186294.jpg', '佛山市适维卫浴有限公司，主要经营卫浴用品、卫浴原材料等，公司注册资本未知，我公司的办公地址位于广东第三大城市，中国近代明清的天下四聚之一佛山，中国 广东 佛山市禅城区 永兴大道，我们有最好的产品和专业的销售和技术团队，我们为客户提供最好的产品、良好的技术支持、健全的售后服务，佛山市适维卫浴有限公司是佛山卫浴用具行业知名企业', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('40', '香港米兰', '1439774754487731988.png', ' 米兰各类制作特色的壁纸是您房间墙壁上的艺术品。 通过与众不同的设计方式，米兰国际三大系列的开山之作定会赢得客户的赞赏，并将公司品牌进一步发扬光大。除了不断良性扩大客户群体米兰国际在之前两大品牌的基础上还多渠道的和国际知名设计师共同合作，创造出令人赞叹的作品。\r\n    追求卓越·家居生活！ \r\n', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('41', '宾利卫浴', '1439789240587386171.jpg', '宾利卫浴系列产品，涵盖卫浴产品，坐便器，虹吸式连体坐便器，直冲式连体坐便器，分体马桶，智能马桶，蹲便器，艺术盆，柱盆，浴室盆，小便斗，花洒，厨房龙头，面盆龙头，浴缸龙头，淋浴龙头，以及角阀，地漏，水嘴，各式妇洗器，花洒，淋浴管，编织管，pvc管等等。产品从设计到装配成品，全部采用优质的原材料和零件，从产品开发到售后服务，每一个产品，每一环节都精工细作，务求完善. ', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('42', '帝陶卫浴', '1439788043797642475.jpg', '广东潮安波意伦陶瓷实业有限公司以“执意创新，健康为本”为立意点，推行绿色环保设计，使出品的“波意伦”、“香陶”卫浴产品始终走在洁具行业的前端，并不断创造出浴室新文化。在环保的基础上根注入了欧美装饰元素，使每件产品都充满优雅、浪漫和高贵的气质。如今波意伦洁具以自己独有的品质和一流的售后服务得到广大用户的深深喜爱，产品畅销国内外各大城市。精心制造、精益求精。我们会跟家努力，不断开发新产品，为大众奉献更多精品，让生活更健康、更精彩。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('43', '东鹏卫浴', '1439788076323916517.jpg', '东鹏卫浴实业有限公司创办于1990年，位于中国著名侨乡、水龙头生产基地-–开平市水口镇，是一家集研发、生产、销售与服务为一体的高档水龙头生产企业。在全国各地建立了二百多个销售网点，自有品牌“东鹏”、“DOPEN”已畅销全国各地，远销欧洲、美洲、东南亚、大洋州等60多个国家和地区，深受国内外众多消费者的青睐和信任。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('44', '方太卫浴', '1439788116696663282.jpg', '方太卫浴国际集团有限公司创办于1996年,是一家集研发,生产,销售于一体的大型企业集团,集团拥有雄厚的技术力量和强大的人才储备,引进德国全自动随道窑炉及梭式窑炉,采用微压注浆全自动生产线等先进设备,公司设立有完善的实验和多道产品测试方法,全面筛查产品的各项功能,确保每一件FOTTIE产品都尽善尽美!', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('45', '汉涛家具', '1439788140322894728.jpg', '汉涛家具公司多年来本着“诚实守信、服务至上”的经营理念，“细心、耐心、周到”的服务态度；“严谨、务实、全面”的管理方针，汉涛新格局装饰创新设计理念、严格管理团队的营销方针，并将十分珍惜广大客户心目中的良好形象，一如继往地珍惜每次服务机会！以人性化的设计理念、精湛的设计工艺、完美的工艺效果，真切而自然的装饰理念为烟台乃至全国的新老客户服务，获得烟台人民的极佳口碑。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('46', '乐尚卫浴', '1439788175587668635.jpg', 'LOSHO-爱上乐尚、优雅、高贵、自信又不失含蓄——德陆风情与东方韵味完美融合，形成典雅和谐、秀外慧中的整体风格，正与您的远见卓识相得益彰。人性化的设计为完美定下了基准。更值得称道的是:乐尚洁具借助公司强大的服务实力，并遵循国际标准，早已在各大、中城市建立了销售及售后服务网络，随时随地，为您提供无微不至的专业级服务。这就是乐尚，不论何种款式，都以无可比拟的领先风范树立起高档洁具典范。主营座便器、柜盆、柱盆、艺术盆、蹲便器,您如果对我们的产品感兴趣或者有任何的疑问，您可以直接给我们留言或直接与我们联络，我们将在收到您的信息后，会第一时间及时与您联络， 我们衷心的希望能与各届朋友合作，携手未来，共享成功的成果！', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('47', '龙塔卫浴', '1439788202514844364.jpg', '龙塔卫浴有限公司是生产“龙塔”牌全铜卫浴洁具系列的专业产家。生产工艺先进，技术力量雄厚，检测手段 完备。致力于新产品的研制，推出异彩纷呈的卫浴精品系列，将给您带来新的生活品味更让您感受家居的优雅与完美。 我公司有、高、中、低档水龙头，卫浴洁具及配套产品“品质保证，终生维护”是我们的承诺，我们力求“以最优的产品，最周 到的服务”来答谢客户的支持和厚爱。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('48', '蒙娜丽莎卫浴', '1439793756517930683.jpg', '公司以自然、简洁、时尚品质生活为设计理念，引进先进的工艺技术，融合天然环保材料，赋予高贵典雅的外观造型，产品深受消费者青睐，掀起卫浴洁具革命性的新时尚。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('49', '万美陶瓷', '1439794174702515168.jpg', '\r\n万美陶瓷地处享有“中国建陶第一镇”美誉的佛山市南庄镇。公司始于2005年，是一家专业生产及经营各种高档抛光砖，不透水釉面砖。釉下彩仿古砖等产品的现代化建陶企业。生产基地占地500多亩，拥有多台世界上先进的意大利sacm17200吨压机，rotocolor滚筒印花机，意大利3d数码喷墨机，及20多条先进的全自动电脑控制生产线。依托规模化的生产实力和强大的科研人才队伍。采用业内领先的工艺技术及管理模式。公司致力于打造国际知名的建陶品牌，专注于高品质的建陶墙地砖的制造和销售，年产各种不同规格的400多个花色品种墙地砖达2000万平方米，营销网络遍布全球各地。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('50', '一鸣特', '1439799072412693531.png', '一鸣特家具自1992年创立以来，在十多年的发展历程中，我们不断进取，勇于创新，发展成为一家集设计、制造、销售和服务为一体的现代化软家具生产企业。公司旗下拥有“一鸣特”和“蝶变”两大品牌。一鸣特沙发和蝶变功能沙发以其时尚、实用的设计理念，优质的产品质量，优异的服务态度，得到了广大经销商及消费者的认可，畅销黑龙江、吉林、辽宁、内蒙古等各省市地区。', 'http://', '50', '1');
