INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('74', '卧虎床垫', '1441350187143421807.png', '哈尔滨市卧虎家具有限公司是一家立足于中国东北地区，放眼于国内乃至世界软体家具市场，优秀且专业的大型软体家具制造企业和服务企业，公司主营项目为中高端床垫、软床产品，已有20余年的发展历史。\r\n卧虎坚持以消费者需求为中心，以向消费者提供优质睡眠承托系统为己任，以生产满足市场不同消费群不同要求的一流品质寝具为目标，坚持严谨的科学研发态度，坚持产品差异化策略、领先策略，构筑了国内一流的产品体系。', 'http://', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('75', '主角家居', '1441350212715123277.jpg', '深圳主角家私有限公司是一家及设计、开发、生产、销售于一体的大型综合性家具企业。公司占地面积100余亩，布局合理，环境优美，具有浓郁的现代气息。 \r\n\r\n      深圳主角家私有限公司自创建之初，坚持“以质量求生存，以信誉赢市场”的经营宗旨，将“打造百年民族品牌”作为企业长期发展战略目标。公司通过IS09001：2000质量管理体系认证，所生产的“主角”品牌家具系列产品，先后荣获“中国著名品牌”、“中国驰名品牌”、“全国质量稳定合格产品”，荣获“全国消费者用户满意企业”。公司先后引进德国、意大利先进木工机械生产设备和欧美的设计理念，运用全新的管理模式，结合高素质的员工队伍，为你提供高档次、高品位的板式卧房系列、客厅系列、餐厅系列、书房系列、高中档沙发、视听柜等系列产品。公司本着“诚信务实、需求至上，竞争双赢、热情服务”的经营理念，对内团结协作，努力进取，对外真诚合作，热情服务，产品深受顾客青睐。公司旗下现拥有E风一族、塞班岛、水木风情、城市森林、梦幻之语等行业知名品牌。几大品牌专卖店连锁全国、遍布大江南北。产品远销俄罗斯、韩国、日本、美国、欧洲、非洲、印度等海外国家。', 'http://', '50', '1');
