INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('77', '远胜办公家具', '1441606879341647882.jpg', '哈尔滨市远胜办公家具是一家集开发，设计，生产，销售于一体的专业办公家具企业。公司拥有高素质的专业设计，生产人员。产品均由先进的机械化生产和静电喷图流水线加工而成，为您量身打造完美的办公家具。\r\n产品包括：文件柜，档案柜，资料柜，更衣柜，工具柜，书架，办公桌椅及院校书柜等。百余种不同型号的产品，尽显时尚风范，为您良好的企业形象锦上添花。\r\n公司本着“质量第一，信誉第一”的原则，以合理的价格，完美的售后服务，足以让您享受远胜柜业的便利。\r\n我公司全体人员热情欢迎您的到来，真诚的与新老客户一道携手共进，共同创造美好的明天。', 'http://', '50', '1');
