INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('87', '国明办公', '1442472752386683218.jpg', '哈尔滨国明办公家具有限公司，是一家主营哈尔滨高档办公家具、哈尔滨校用家具的企业，是办公家具企业龙头，销售哈尔滨屏风隔断、哈尔滨办公沙发、哈尔滨文件柜、哈尔滨餐桌椅等产品。\r\n　　并通过了IS09000、IS014000认证。本企业严格按照“质量环境管理体系”实施执行，以质取胜，诚信厚实，追求卓越，超越自我是国明人永恒的追求。', 'http://', '50', '1');
